DROP TABLE IF EXISTS customers_data;
CREATE TABLE customers_data (
customers_data_id int(11) NOT NULL AUTO_INCREMENT,
customers_id int(11) NOT NULL,
customers_data_key varchar(80) NOT NULL,
customers_data_value varchar(255) DEFAULT NULL,
PRIMARY KEY (customers_data_id),
UNIQUE KEY customers_id_data_key (customers_id, customers_data_key)
) CHARACTER SET utf8 COLLATE utf8_unicode_ci;

DROP TABLE IF EXISTS customer_data_groups;
CREATE TABLE customer_data_groups (
customer_data_groups_id int(11) NOT NULL,
language_id int(11) NOT NULL,
customer_data_groups_name varchar(255) NOT NULL,
cdg_vertical_sort_order int(11) NOT NULL,
cdg_horizontal_sort_order int(11) NOT NULL,
customer_data_groups_width int(11) NOT NULL,
PRIMARY KEY (language_id, customer_data_groups_id)
) CHARACTER SET utf8 COLLATE utf8_unicode_ci;


DELETE FROM configuration WHERE configuration_group_id = 5 AND configuration_key IN ('ACCOUNT_GENDER', 'ACCOUNT_DOB', 'ACCOUNT_COMPANY', 'ACCOUNT_SUBURB', 'ACCOUNT_STATE');
DELETE FROM configuration WHERE configuration_group_id = 2 AND configuration_key IN ('ENTRY_FIRST_NAME_MIN_LENGTH', 'ENTRY_LAST_NAME_MIN_LENGTH', 'ENTRY_DOB_MIN_LENGTH', 'ENTRY_EMAIL_ADDRESS_MIN_LENGTH', 'ENTRY_STREET_ADDRESS_MIN_LENGTH', 'ENTRY_COMPANY_MIN_LENGTH', 'ENTRY_POSTCODE_MIN_LENGTH', 'ENTRY_CITY_MIN_LENGTH', 'ENTRY_STATE_MIN_LENGTH', 'ENTRY_TELEPHONE_MIN_LENGTH', 'ENTRY_PASSWORD_MIN_LENGTH', 'MIN_DISPLAY_BESTSELLERS');

UPDATE configuration SET configuration_value = 'True' WHERE configuration_key LIKE 'MODULE\_ORDER\_TOTAL\_SHIPPING\_%' AND configuration_value = 'true';
UPDATE configuration SET configuration_value = 'False' WHERE configuration_key LIKE 'MODULE\_ORDER\_TOTAL\_SHIPPING\_%' AND configuration_value = 'false';

UPDATE address_format SET address_format = REPLACE(address_format, "$firstname $lastname", "$name");

